mapper.update:
UPDATE operator
SET operator_password = #{newPassword}
where operator_status = 1 and id = #{userId} and operator_password = #{loginPassword}

mapper.select:
SELECT
u.id AS userId,
u.operator_no AS userNo,
u.operator_name as userName,
u.operator_loginName AS loginName,
u.operator_password AS password
FROM operator u


        and u.operator_loginName = #{loginName}


        and u.operator_password = #{password}


        and u.id = #{userId}


        and u.operator_status = #{status}


        and u.operator_status = 1


limit 1
